import React, { useState } from "react";
import "../data.json";
import arrowslide from "../assets/arrowSlide.png"

import "../styles/components-style/SlideShow.css"


function SlideShow ({views}) {

    return (
        <section>
            <div className="containerSlide">
        
            <div className="buttonSlide">
            <img className="prevButton" src={arrowslide} alt="arrow prev" />
            <img className="nextButton" src={arrowslide} alt="arrow next" />
            </div>
            
            {views.map((picture, index) => (
                <img key={index} src={picture} alt="galerie de la location" className= "sliderItem" />
            ))}
        </div>
        </section>
    )
}

export default SlideShow;
